function getCookie(e){let o=null;if(document.cookie&&""!==document.cookie){const t=document.cookie.split(";");for(let n=0;n<t.length;n++){const c=t[n].trim();if(c.substring(0,e.length+1)===e+"="){o=decodeURIComponent(c.substring(e.length+1));break}}}return o}function LoadReactions(e){$(`#form-reaction-${e}`).load(`/reaction/react?urlhash=${e}`)}function React(e,o){let t=$(`#form-reaction-${e}`),n=t.prop("method"),c=t.prop("action");$.ajax({type:n,url:c,data:{urlhash:e,react_slug:o},headers:{"X-Requested-With":"XMLHttpRequest","X-CSRFToken":getCookie("csrftoken")},success:function(){LoadReactions(e)},error:function(){alert("ERROR in Reaction!")}})}$(document).ready((function(){$('[id^="form-reaction-"]').each((function(){LoadReactions(this.id.replace("form-reaction-",""))}))}));